!function(){const c=document.querySelector("#results"),o=document.querySelector(".character-image"),a=document.querySelector(".character-name"),i=document.querySelector(".birth-year"),s=document.querySelector(".eye-color"),l=document.querySelector(".gender"),h=document.querySelector(".hair-color"),u=document.querySelector(".height"),m=document.querySelector(".mass"),d=document.querySelector(".skin-color");fetch("https://swapi.dev/api/people/?format=json").then(function(e){e?e.json().then(function(e){for(var r=e.results,n=0;n<r.length;n++)c.innerHTML+='<li class="character-item"> <a href="'+r[n].url+"\"><img class='avatar' src='./assets/images/"+r[n].name+".png' /> <div class='character-info'>"+r[n].name+"<br> <span>"+r[n].gender+"</span> </div> </a> </li>";for(var t=document.getElementsByTagName("a"),n=0;n<t.length;n++)t[n].addEventListener("click",function(e){e.preventDefault(),e=this.getAttribute("href"),fetch(e).then(function(e){e?e.json().then(function(e){o.innerHTML="<img src='./assets/images/"+e.name+".png'/>",a.innerHTML="Name: "+e.name,i.innerHTML="Birth year: "+e.birth_year,s.innerHTML="Eye color: "+e.eye_color,l.innerHTML="Gender: "+e.gender,h.innerHTML="Hair Color: "+e.hair_color,u.innerHTML="Height: "+e.height,m.innerHTML="Mass: "+e.mass,d.innerHTML="Skin Color: "+e.skin_color}).catch(function(e){console.error("Fetch character data failed! "+e)}):console.error("Fetch res failed!")}).catch(function(e){console.error("Fetch character "+e)})})}):console.error("Get res failed!")}).catch(function(e){console.error("Fetch characters failed",e)})}();